[SuppressUnmanagedCodeSecurity,SkipLocalsInit, StructLayout(LayoutKind.Sequential ,Pack = 4)]
public unsafe static partial class VkHelper
{
	public static void Check(this VkResult result, string message="")
    {
        if (result != VkResult.VK_SUCCESS){
            // Log.Critical($"{message} => Vulkan Error :{ result }");
            throw new Exception($"{message} => Vulkan Error :{ result }"  );
        }
    }

